uint8_t get_level(uint8_t bite){
	if(bite){
		return HIGH;
	}
	else{
		return LOW;
	}
}

void fill_buffer(int color, uint8_t lower, uint8_t upper){

	uint8_t bites[32];
	get_bites(color, bites);
	
	for(uint16_t pixel = lower; pixel<upper; pixel++){
		for(uint8_t bite = 0; bite<32; bite++){
			BUF_DMA[pixel*32 + bite] = get_level(bites[bite]);
		}

	}
	
	if(lower!=0){
		for(uint8_t i = 0; i<lower*32; i++){
			BUF_DMA[i] = LOW;
		}		
	}

	
	for(uint16_t i = upper*32; i<ARRAY_LEN-DELAY_LEN; i++){
		BUF_DMA[i] = LOW;
	}
}





